function toggleAnswer(e){const n=document.getElementById(`faq-answer${e}`);"none"===n.style.display||""===n.style.display?n.style.display="block":n.style.display="none"}document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".faq-question").forEach((function(e,n){e.addEventListener("click",(function(){toggleAnswer(n+1)}))}))}));const button=document.getElementById("button-center-item"),handleContinueClick=()=>{window.location.href="./index.html"};button.addEventListener("click",handleContinueClick,{capture:!1});