function toggleAnswer(e){const n=document.getElementById(`faq-answer${e}`);"none"===n.style.display||""===n.style.display?n.style.display="block":n.style.display="none"}document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelectorAll(".faq-question"),n=document.querySelectorAll(".faq-answer");for(let n=0;n<e.length;n++)e[n].id="faq-question"+(n+1);for(let e=0;e<n.length;e++)n[e].id="faq-answer"+(e+1);e.forEach((function(e,n){e.addEventListener("click",(function(){toggleAnswer(n+1)}))}))}));const button=document.getElementById("button-center-item"),handleContinueClick=()=>{window.location.href="./index.html"};button.addEventListener("click",handleContinueClick,{capture:!1});